  PRIMARY KEY  (`product_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `jos_vm_product_reviews`;
CREATE TABLE `jos_vm_product_reviews` (
